$(document).ready(function(){$("#registerButton").click(function(e){$("#errorAlert").fadeOut("fast");$(this).prop("disabled",true);e.preventDefault();var g=$("#inputFirstname").val();var d=$("#inputLastname").val();var c=$("#inputMail").val();var b=$("#inputPassword").val();var f=$("#inputPasswort2").val();var a="";if(g<2){a+="<li>Please enter your Firstname</li>"}if(d<2){a+="<li>Please enter your Lastname</li>"}if(c<2){a+="<li>Please enter a correct E-Mail</li>"}if(b<6){a+="<li>Please use a password with at least 8 digits</li>"}else{if(b!=f){a+="<li>The passwords are not similar</li>"}}if(a!=""){$("#errorText").html(a);$("#errorAlert").fadeIn("fast");$(this).prop("disabled",false)}else{$.ajax({type:"POST",data:{firstname:g,lastname:d,email:c,password:b,password2:f},url:"modul/register/sendRegistration.php",success:function(h){if(h){$("#errorText").html(h);$("#errorAlert").fadeIn("fast");$("#registerButton").prop("disabled",false)}else{$("#successText").html("Successfully Registered. You can now Login.");$("#successAlert").fadeIn("fast");$("#regForm").slideUp("fast")}}})}})});